(()=>{const e=document.querySelector("#list");window.onload=void[{description:"wash the dishes",completed:!1},{description:"complete To Do list project",completed:!1}].forEach(((i,o)=>{i.index=o,e.innerHTML+=`<li class="list-item" id="${o}">\n    <input type="checkbox" value="${i.completed}">${i.description}\n    </li>`}))})();